tC.setPropertyValue("ETag", ETag);
//tC.setPropertyValue("Expires", Expires);
//tC.setPropertyValue("Last-Modified", Last_Modified);
tC.setPropertyValue("x-Transaction-Info", x_Transaction_Info);
}

