			assert 1 == 2;
		}
	break;
	case "400":  
		if (httpStatus != "HTTP/1.1 400 Bad Request") {
			assert 1 == 2;
		}
	break;
	case "401":  
		if (httpStatus != "HTTP/1.1 401 Unauthorized") {
			assert 1 == 2;
		}
	break;
	case "403":  
		if (httpStatus != "HTTP/1.1 403 Forbidden") {
			assert 1 == 2;
		}
	break;
	case "404":  
		if (httpStatus != "HTTP/1.1 404 Not Found") {
			assert 1 == 2;
		}
	break;
	case "405":  
