			assert 1 == 2;
		}
	break;
	case "409":  
		if (httpStatus != "HTTP/1.1 409 Conflict") {
			assert 1 == 2;
		}
	break;
	case "304":  
		if (httpStatus != "HTTP/1.1 304 Not Modified") {
			assert 1 == 2;
		}
	break;
	default:
	 		assert 1 == 2;
	break; 	

}

