		case "Cache-Control": 
			Cache_Control = value[0];
			break;
		case "ETag": 
			ETag = value[0]; 
			break;
		/*case "Expires": 
			Expires = value[0]; 
			break;
		case "Last-Modified": 
			Last_Modified = value[0]; 
			break;*/
		case "x-Transaction-Info": 
			x_Transaction_Info = value[0]; 
			break;


	}
}


