					break;*/
				case "x-Transaction-Info": 
					x_Transaction_Info = value[0]; 
					break;
				case "Vary": 
					Vary = value[0]; 
					break;

			}
		}

		tC.setPropertyValue("Cache-Control", Cache_Control);
		tC.setPropertyValue("ETag", ETag);
