log.info filtercount

def responseAsXml = context.expand( '${GET-MetadataBasicList#ResponseAsXml#declare namespace ns1=\'${#Project#Coordinator_Schema}\'; //ns1:BasicAssetList[1]/ns1:BasicAssetReference[1]}' )

