            				catch(Throwable e)
            				{
            					log.info e;
            					log.info DiscreteMediaTokenIDExp;
            					log.info ExpirationDateTimeExp;
            					log.info ExpirationDateTimeAct;	
            					iFailed = true;		
            				}
            			}

            			StatusAct = actDMT.DiscreteMediaToken[countDMT].ResourceStatus.Current.Value.text();
            			try
            			{
            				assert StatusAct == StatusExp;
            			}
            			catch(Throwable e)
            			{
            				log.info e;
            				log.info DiscreteMediaTokenIDExp;
            				log.info StatusExp;
            				log.info StatusAct;
            				iFailed = true;
            			}        			

            			if(DeletionDateExp != null)
    				    {
            				DeletionDateAct = actDMT.DiscreteMediaToken[countDMT].ResourceStatus.Current.@DeletionDate.text().replace("T", " ").replace("Z", "");        				
            				try
            				{
            					assert DeletionDateAct == DeletionDateExp;
            				}
            				catch(Throwable e)
            				{
            					log.info e;
            					log.info DiscreteMediaTokenIDExp;
            					log.info DeletionDateExp;
            					log.info DeletionDateAct;
            					iFailed = true;
            				}
    				    }

        				if(DeletedByExp != null)
        				{        
                				DeletedByAct = actDMT.DiscreteMediaToken[countDMT].ResourceStatus.Current.@DeletedBy.text();        			
                				try
                				{
                					assert DeletedByAct == DeletedByExp;
                				}
                				catch(Throwable e)
                				{
                					log.info e;
                					log.info DiscreteMediaTokenIDExp;
                					log.info DeletedByExp;
                					log.info DeletedByAct;
                					iFailed = true;
                				}
        				}

            			StatusDescriptionAct = actDMT.DiscreteMediaToken[countDMT].ResourceStatus.Current.Description.text();
            			try
            			{            				
            				assert StatusDescriptionAct == StatusDescriptionExp;        				
            			}
            			catch(Throwable e)
            			{
            				log.info e;
            				log.info DiscreteMediaTokenIDExp;
            				log.info StatusDescriptionExp;
            				log.info StatusDescriptionAct;
            				iFailed = true;
            			}           	
                    }      		

        			if(nodeRole == 'urn:dece:role:retailer' || nodeRole == 'urn:dece:role:dsp' || nodeRole == 'urn:dece:role:portal' || nodeRole == 'urn:dece:role:portal:customersupport' || nodeRole == 'urn:dece:role:retailer:customersupport' || nodeRole == 'urn:dece:role:dsp:customersupport' || nodeRole == 'urn:dece:role:coordinator' || nodeRole == 'urn:dece:role:coordinator:customersupport' || nodeRole == 'urn:dece:role:dece' || nodeRole == 'urn:dece:role:dece:customersupport')
        			{                        				
        				if((badDMTFound == "yes" || TypeExp == "urn:dece:type:status:discretemediaright:available") && (nodeRole == 'urn:dece:role:retailer' || nodeRole == 'urn:dece:role:dsp' || nodeRole == 'urn:dece:role:portal' || nodeRole == 'urn:dece:role:portal:customersupport' || nodeRole == 'urn:dece:role:retailer:customersupport' || nodeRole == 'urn:dece:role:dsp:customersupport' || nodeRole == 'urn:dece:role:coordinator' || nodeRole == 'urn:dece:role:coordinator:customersupport' || nodeRole == 'urn:dece:role:dece' || nodeRole == 'urn:dece:role:dece:customersupport'))
        				{        					
        					sqlStrCountDMTH = """select
        						count(*)
        						from
        						discrete_media_token_history dmth,
        						discrete_media_token dmt
        						where
        						dmth.DISCRETE_MEDIA_TOKEN_OID = hextoraw('$DiscreteMediaTokenOidExp')
                         		and
                         		dmt.DISCRETE_MEDIA_TOKEN_OID = dmth.DISCRETE_MEDIA_TOKEN_OID
                         		and
                         		dmth.state != 'available'""";

                         	sql.eachRow(sqlStrCountDMTH){
        						countDMTH = it[0];
        					}
        				}
        				else
        				{           					     					
        					sqlStrCountDMTH = """select
        					count(*)
        					from
        					discrete_media_token_history dmth,
        					discrete_media_token dmt
        					where
        					dmth.DISCRETE_MEDIA_TOKEN_OID = hextoraw('$discreteMediaTokenOid')
                         	and
                         	dmt.DISCRETE_MEDIA_TOKEN_OID = dmth.DISCRETE_MEDIA_TOKEN_OID
        					and        				
        					(dmth.status != dmt.status 
        					or 
        					dmth.state != dmt.state
        					or        					
						dmth.ACCOUNT_OID != dmt.ACCOUNT_OID
						or
						dmth.USER_OID != dmt.USER_OID
						or
						dmth.RIGHTS_TOKEN_OID != dmt.RIGHTS_TOKEN_OID
						or
						dmth.PURCHASED_MEDIA_PROFILE_OID != dmt.PURCHASED_MEDIA_PROFILE_OID
						or
						dmth.EXPIRATION_DATE != dmt.EXPIRATION_DATE
						--or
						--dmth.CREATED_BY != dmt.CREATED_BY
						or
						dmth.CREATED_DATE != dmt.CREATED_DATE
						or
						dmth.CLOSED_BY != dmt.CLOSED_BY
						or
						dmth.CLOSED_DATE != dmt.CLOSED_DATE)""";				

        					sql.eachRow(sqlStrCountDMTH){
        						countDMTH = it[0];        						
        					}	
        				}

        			}        												
        			try
        			{
        				assert actDMT.DiscreteMediaToken[countDMT].ResourceStatus.History.Prior.size() == countDMTH;
        			}
        			catch(Throwable e)
        			{
        				log.info e;
        				log.info DiscreteMediaTokenIDExp;
        				log.info countDMTH;
        				log.info actDMT.DiscreteMediaToken[countDMT].ResourceStatus.History.Prior.size();
        				iFailed = true;
        			}        			        			
        			countDMTH = 0;				 			
        			for (int countDMTH = 0; countDMTH < actDMT.DiscreteMediaToken[countDMT].ResourceStatus.History.Prior.size(); countDMTH++) 
        			{
        				if(badDMTFound == "yes")
        				{        						 
        					sqlStrDMTH = """select
        					dmth.discrete_media_token_oid,
            				dmth.status HistoryStatus,
            				(case
            				when dmth.CLOSED_DATE is null then (case when dmth.CREATED_DATE is null then substr(to_char(dmth.CREATED_DATE, 'yyyy-mm-dd hh24:mi:ss.FF'), 1, 23) else substr(to_char(dmth.CREATED_DATE, 'yyyy-mm-dd hh24:mi:ss.FF'), 1, 23) end)
            				else
            	  		 	substr(to_char(dmth.CLOSED_DATE, 'yyyy-mm-dd hh24:mi:ss.FF'), 1, 23) 
            				end) HistoryStatusModificationDate,
            				(case
            				when dmth.closed_by is null then (case when dmth.CREATED_BY is null then 'urn:dece:org:org:dece:' || dmth.created_by else 'urn:dece:org:org:dece:' || dmth.CREATED_BY end)
            				else
            				'urn:dece:org:org:dece:' || dmth.closed_by
            				end) HistoryStatusModifiedBy,
            				dmth.state HistoryStatusDescription
            				from 
            				discrete_media_token_history dmth,
            				discrete_media_status_ref dmsr,
            				DISCRETE_MEDIA_TOKEN dmt
            				where
            				dmth.DISCRETE_MEDIA_TOKEN_OID = hextoraw('$discreteMediaTokenOid')
            				and
            				dmt.DISCRETE_MEDIA_TOKEN_OID = dmth.DISCRETE_MEDIA_TOKEN_OID
            				and
            				dmth.state != 'available'
            				order by        				
            				dmth.state asc""";
        				}								
        				else
        				{
        					sqlStrDMTH = """select
            					dmth.discrete_media_token_oid,
            					dmth.status HistoryStatus,
            					(case
            					when dmth.CLOSED_DATE is null then (case when dmth.CREATED_DATE is null then substr(to_char(dmth.CREATED_DATE, 'yyyy-mm-dd hh24:mi:ss.FF'), 1, 23) else substr(to_char(dmth.CREATED_DATE, 'yyyy-mm-dd hh24:mi:ss.FF'), 1, 23) end)
            					else
            	  		 		substr(to_char(dmth.CLOSED_DATE, 'yyyy-mm-dd hh24:mi:ss.FF'), 1, 23) 
            					end) HistoryStatusModificationDate,
            					(case
            					when dmth.closed_by is null then (case when dmth.CREATED_BY is null then 'urn:dece:org:org:dece:' || dmth.created_by else 'urn:dece:org:org:dece:' || dmth.CREATED_BY end)
            					else
            					'urn:dece:org:org:dece:' || dmth.closed_by
            					end) HistoryStatusModifiedBy,
            					dmth.state HistoryStatusDescription
            					from 
            					discrete_media_token_history dmth,
            					discrete_media_status_ref dmsr,
            					DISCRETE_MEDIA_TOKEN dmt
            					where
            					dmth.DISCRETE_MEDIA_TOKEN_OID = hextoraw('$discreteMediaTokenOid')
            					and
            					dmt.DISCRETE_MEDIA_TOKEN_OID = dmth.DISCRETE_MEDIA_TOKEN_OID
            					and
            					(dmth.status != dmt.status 
        						or 
        						dmth.state != dmt.state
        						or        					
							dmth.ACCOUNT_OID != dmt.ACCOUNT_OID
							or
							dmth.USER_OID != dmt.USER_OID
							or
							dmth.RIGHTS_TOKEN_OID != dmt.RIGHTS_TOKEN_OID
							or
							dmth.PURCHASED_MEDIA_PROFILE_OID != dmt.PURCHASED_MEDIA_PROFILE_OID
							or
							dmth.EXPIRATION_DATE != dmt.EXPIRATION_DATE
							--or
							--dmth.CREATED_BY != dmt.CREATED_BY
							or
							dmth.CREATED_DATE != dmt.CREATED_DATE
							or
							dmth.CLOSED_BY != dmt.CLOSED_BY
							or
							dmth.CLOSED_DATE != dmt.CLOSED_DATE)""";
        				}

        				sql.eachRow(sqlStrDMTH){										
        					HistoryStatusExp = "urn:dece:type:status:" + it[1];
        					HistoryStatusModificationDateExp = it[2];
        					HistoryStatusModifiedByExp = it[3];
        					HistoryStatusDescriptionExp = "urn:dece:type:status:discretemediaright:" + it[4];	

        					HistoryStatusAct = actDMT.DiscreteMediaToken[countDMT].ResourceStatus.History.Prior[countDMTH].Value.text();        					    													
        					if(HistoryStatusAct == HistoryStatusExp)
        					{						
        						try
        						{
        							assert HistoryStatusAct == HistoryStatusExp;        							
        						}
        						catch(Throwable e)
        						{
        							log.info e;
        							log.info DiscreteMediaTokenIDExp;
        							log.info HistoryStatusExp;
        							log.info HistoryStatusAct;
        							iFailed = true;
        						}

        						HistoryStatusModificationDateAct = actDMT.DiscreteMediaToken[countDMT].ResourceStatus.History.Prior[countDMTH].@ModificationDate.text().replace("T", " ").replace("Z", "");
        						try
        						{
        							assert HistoryStatusModificationDateAct == HistoryStatusModificationDateExp;
        						}
        						catch(Throwable e)
        						{
        							log.info e;
        							log.info DiscreteMediaTokenIDExp;
        							log.info HistoryStatusExp;
        							log.info HistoryStatusModificationDateExp;
        							log.info HistoryStatusModificationDateAct;
        							iFailed = true;
        						}

        						HistoryStatusModifiedByAct = actDMT.DiscreteMediaToken[countDMT].ResourceStatus.History.Prior[countDMTH].@ModifiedBy.text();
        						try
        						{
        							assert HistoryStatusModifiedByAct == HistoryStatusModifiedByExp;        							
        						}
        						catch(Throwable e)
        						{
        							log.info e;
        							log.info DiscreteMediaTokenIDExp;
        							log.info HistoryStatusExp;
        							log.info HistoryStatusModifiedByExp;
        							log.info HistoryStatusModifiedByAct;
        							iFailed = true;
        						}

        						HistoryStatusDescriptionAct = actDMT.DiscreteMediaToken[countDMT].ResourceStatus.History.Prior[countDMTH].Description.text();
        						try
        						{
        							assert HistoryStatusDescriptionAct == HistoryStatusDescriptionExp;
        						}
        						catch(Throwable e)
        						{
        							log.info e;
        							log.info DiscreteMediaTokenIDExp;
        							log.info HistoryStatusDescriptionExp;
        							log.info HistoryStatusDescriptionAct;	
        							iFailed = true;
        						}						 
        					}	
        					countDMTH = countDMTH + 1;		   	
        				}			
        			}			
			} 
			else
			{				
				iFailed = true;
			}
			countDMT = countDMT + 1
		}			 
	}
}

assert iFailed == false;

sql.close();

log.info "Woohoo!";

