	substr(to_char(dmt.EXPIRATION_DATE, 'yyyy-mm-dd hh24:mi:ss.FF'), 1, 23) ExpirationDateTime,
	'urn:dece:type:status:' || dmt.status STATUS,	
	substr(to_char(dmt.CLOSED_DATE, 'yyyy-mm-dd hh24:mi:ss.FF'), 1, 23) DeletionDate,
