	}			
}
else
{
	try
	{
		actDMT = new XmlSlurper().parseText(actXML);
		log.info 'actDMT == '+actDMT
	}
	catch (Throwable e)
	{
		log.info e;	
	}	
	try
	{	
		log.info 'actDMT.DiscreteMediaToken.size() ==='+actDMT.DiscreteMediaToken.size()
		log.info 'countDMT == '+countDMT	
		assert actDMT.DiscreteMediaToken.size() == countDMT;
	}
	catch(Throwable e)
	{
		log.info e;
		log.info countDMT;
