		iFailed = true;
	}	
     countDMT = 0;	 
	for (int countDMT = 0; countDMT < actDMT.DiscreteMediaToken.size(); countDMT++) 
	{			
		sql.eachRow(sqlStrDMT){				
			CreatedDateTimeExp = it[0];
			log.info 'CreatedDateTimeExp=='+CreatedDateTimeExp
			CreatedByExp = it[1];
			TypeExp = it[2];
			DiscreteMediaTokenIDExp = it[3];		
			RequestingUserIDExp = it[4];
			RightsTokenIDExp = it[5];
			DiscreteMediaProfileExp = it[6];
			ContentProfileExp = it[7];
			ExpirationDateTimeExp = it[8];
			StatusExp = it[9];			
    			DeletionDateExp = it[10];		
    			DeletedByExp = it[11];
    			StatusDescriptionExp = it[12];
    			log.info 'StatusDescriptionExp === ' +StatusDescriptionExp
        		ModificationDateExp = it[13];
        		ModifiedByExp = it[14];
        		dmRightsRemain = it[15];	
			log.info 'dmRightsRemain11 == '+dmRightsRemain


			DiscreteMediaTokenIDAct = actDMT.DiscreteMediaToken[countDMT].@DiscreteMediaTokenID.text();																	

			if(DiscreteMediaTokenIDAct == DiscreteMediaTokenIDExp)
			{					
				log.info 'DiscreteMediaTokenIDAct == '+DiscreteMediaTokenIDAct
				log.info 'DiscreteMediaTokenIDExp == '+DiscreteMediaTokenIDExp
				DiscreteMediaTokenOidExp	= DiscreteMediaTokenIDExp.split(":")[5];
				for (int i = 0; i < propertiesList.size(); i++) 
				{	
					if(propertiesList[i].name.length() == 22 && propertiesList[i].name[0..20] == 'DiscreteMediaTokenOid')
					{						
						discreteMediaTokenOid = propertiesList[i].value.split(":")[0];												
						if(discreteMediaTokenOid == DiscreteMediaTokenOidExp)
						{
  							badDMTFound = propertiesList[i].value.split(":")[1];
  							dmRightsRemainExp = propertiesList[i].value.split(":")[2];
  							countDMTH = propertiesList[i].value.split(":")[3].toInteger();
						}
  					}	
				}																																					
        			try
        			{        				       				
        				log.info 'DiscreteMediaTokenIDAct === '+DiscreteMediaTokenIDAct
        				log.info 'DiscreteMediaTokenIDExp === '+DiscreteMediaTokenIDExp
        				assert DiscreteMediaTokenIDAct == DiscreteMediaTokenIDExp;
        			}
        			catch(Throwable e)
