def jDBCDriver = context.expand( '${#Project#JDBCDriver}' )
def db = [url:jDBCConnStr, user: userName, password: passWord, driver:jDBCDriver];

def sql = Sql.newInstance(db.url, db.user, db.password, db.driver);

def nodeOid = testCase.getPropertyValue("NodeOid");

def nodeAccountOid = testProject.getPropertyValue("NodeAccountOid");
sqlStr = """update node_account set node_oid = (select node_oid from node where node_oid not in ($nodeOid) and rownum < 2) where node_account_id = hextoraw('$nodeAccountOid') and rownum < 2""";
sql.executeUpdate(sqlStr);

sql.close();

