	case "${urnRating}us:mpaa:pg":
	case "${urnRating}us:mpaa:pg-13":
	case "${urnRating}us:mpaa:r":
	case "${urnRating}us:mpaa:nc-17":
	case "${urnRating}us:mpaa:nr":
	case "${urnRating}us:mpaa:m":
