	switch (header) {
		case "Location": 
			log.info "versionMinor: "+tP.getPropertyValue("versionMinor");
			if (tP.getPropertyValue("versionMinor")=="06" || tP.getPropertyValue("versionMinor")=="07"){
			contentLocation = URLDecoder.decode(value[0], "UTF-8");
