		if (httpStatus != "HTTP/1.1 409 Conflict") {
			assert 1 == 2;
		}
	break;
	case "412":  
		if (httpStatus != "HTTP/1.1 412 Precondition Failed") {
			assert 1 == 2;
		}
	break;
	default:
	 		assert 1 == 2;
	break; 	

}

