String url = "";

switch (urlEndPoint) {
	default:
		url = "https://" + tP.getPropertyValue("targetHost").toString() + ":" + 
			tP.getPropertyValue("targetPort").toString();
	break;
}

log.info "url[$url]";

testRunner.testCase.getTestStepByName('POST - STS').httpRequest.setEndpoint(url)

