		Resp_RightsTokenID = Val_RightsResXml.RightsTokenReference[rtc].@RightsTokenID;


		 DBrightstokenid = (Val_RightsResXml.RightsTokenReference[rtc].@RightsTokenID).toString().tokenize(':').last();
	}
