	tC.setPropertyValue("counter",counter.toString());
	testRunner.gotoStepByName("Call STS Get");
} else {
	if (counter == loopuntil) {
		tC.setPropertyValue("counter",counter.toString());
		testRunner.gotoStepByName("Call STS Get");
	}
}

