def loopuntil = tC.getPropertyValue("loopuntil").toInteger();



if (counter == 1) {
	a = new dbUtils.dbSAMLAssertion(log,tP.getPropertyValue("targetSystem"));

	log.info "nodeid[$nodeid]";


	assert a.samlSetStatusDeleted(assertId, nodeid) == 1;
}

