def loopuntil = tC.getPropertyValue("loopuntil").toInteger();

counter++;

if (counter <= loopuntil) {
	tC.setPropertyValue("counter",counter.toString());
	testRunner.gotoStepByName("Call STS");
} else {
	if (counter == loopuntil) {
		tC.setPropertyValue("counter",counter.toString());
		testRunner.gotoStepByName("Call STS");
	}
}

