	genUtils = new generalUtils.generalUtils(log);
	ds = genUtils.randomUUID();
}
catch (Throwable e) {
	log.info "Error during genUtils.randomUUID - $e";
}
finally {
	tC.setPropertyValue("deviceDeviceString", ds);
