	def atoken = new dbUtils.dbDeviceAuthToken(log,tP.getPropertyValue("targetSystem"));

	atoken.deviceDeleteTokensByNodeOid(node_oid);
}
catch (Throwable e) {
	this.log.info "Error during dbDeviceAuthToken - $e";
}

