{              
     line = lines[i];
          try {
                                if(line.trim()[0..16] == "<saml2:Assertion ")
                                {
                                                startLine = i;
                                                //log.info startLine;
                                }
                                else if(line.trim()[0..17] == "</saml2:Assertion>")
                                {
                                                endLine = i;
                                                //log.info endLine;
                                }              
          }
          catch(Throwable e){
                                //Do Something
          }              
}
for(j = startLine; j <= endLine; j++){
	XMLassertion.append(lines[j]);
}

