*  Added this override with the 7.7 drop.  They change the name
*  of the <ErrorLink> host.
*/
if (context.expand( '${#Project#nodeIdRTL}' ).length() == 0) {
	errorHost = "http://error.decellc.org";
} else {
	errorHost = context.expand( '${#Project#nodeIdRTL}' );
