	log.info "sql is [$sql]";
	resultsDB = dbUtils.SelectFromDB(sql,tP.getPropertyValue("targetSystem"));
} catch (Throwable e) {
	log.info "ERROR - SelectFromDB issue! - $e";
} finally {
	log.info "results is [$resultsDB]";
}

