		errorReason = "The request is not authorized.";
		break;
	case "FORBIDDEN":
	default:
		errorReason="Unknown Error";
		errorID = "Forbidden";
		errorLink = "";
		orgRequest = "";
		doContentLocaton  = false;
		dobodyXML = true;
	break;
}

// HTTP header status
