	results = a.SelectFromDB(sql,tP.getPropertyValue("targetSystem"));

	myXML = new XmlSlurper().parseText(results);

	if (myXML.ResultSet.Row.size() == 1) {
		nodeSamlRowCount = myXML.ResultSet.Row.CNT.text().toInteger();
	} else {
		nodeSamlRowCount = 1;
	}
}
