import com.eviware.soapui.model.testsuite.TestRunner.Status;

def tC = testRunner.getTestCase();
def tS = tC.getTestSuite();
def tP = tS.getProject();

def myTC = tP.getTestSuiteByName("Common Test Cases").getTestCaseByName("Set PEM File");

myTC.setPropertyValue("tName", tC.getName());
myTC.setPropertyValue("sName", tS.getName());
myTC.setPropertyValue("testStepName", "PolicyDeleteULC");

def runner = myTC.run(null, false);

assert runner.status == Status.FINISHED;

def useURLEncoding = tP.getPropertyValue("useURLEncoding");
if(useURLEncoding == "No")
{
	tC.setPropertyValue("nodeAccountIdURL", tC.getPropertyValue("nodeAccountId"));
	tC.setPropertyValue("nodeUserIdURL", tC.getPropertyValue("nodeUserId"));
	tC.setPropertyValue("policyClassURL", tC.getPropertyValue("policyClass"));
	tC.setPropertyValue("policyIdURL", tC.getPropertyValue("policyId"));
	tC.setPropertyValue("policyListIdURL", tC.getPropertyValue("policyListId"));
}

//import urlFunctions.urlFunctions;

def tC = testRunner.getTestCase();
def tS = tC.getTestSuite();
def tP = tS.getProject();
def tStep  = tC.getTestStepByName('AccountUserPolicyCreate');
def expXML = tStep.httpRequest.response.getResponseContent();

String status = "";
String connection = "";
String contentType = "";
String contentLength = "";
String contentLocation = "";

tStep.httpRequest.response.responseHeaders.each() { header, value ->
	switch (header) {
		case "Location": 
			contentLocation = value[0];
			break;
	}
}

tC.setPropertyValue("httpLocation", contentLocation);
tC.setPropertyValue("nodeAccountId", contentLocation.split("/Account/")[1].split('/User/')[0]);
tC.setPropertyValue("nodeUserId", contentLocation.split('/User/')[1]);

tC.setPropertyValue("nodeAccountIdURL",tC.getPropertyValue("nodeAccountId"));
tC.setPropertyValue("nodeUserIdURL", tC.getPropertyValue("nodeUserId"));

def toTCName = tC.getPropertyValue("tName");
def toTSName = tC.getPropertyValue("sName")

def toTC = testRunner.testCase.testSuite.project.getTestSuiteByName(toTSName).getTestCaseByName(toTCName);
log.info toTC.getName();
log.info tC.getPropertyValue("nodeAccountId")

toTC.setPropertyValue("nodeAccountIdURL",tC.getPropertyValue("nodeAccountId"));
toTC.setPropertyValue("nodeUserIdURL", tC.getPropertyValue("nodeUserId"));
toTC.setPropertyValue("nodeAccountId",tC.getPropertyValue("nodeAccountId"));
toTC.setPropertyValue("nodeUserId", tC.getPropertyValue("nodeUserId"));

tC.setPropertyValue("nodeAccountIdURL1",tC.getPropertyValue("nodeAccountId"));
tC.setPropertyValue("nodeUserIdURL1", tC.getPropertyValue("nodeUserId"));
tC.setPropertyValue("nodeAccountId1",tC.getPropertyValue("nodeAccountId"));
tC.setPropertyValue("nodeUserId1", tC.getPropertyValue("nodeUserId"));

