import CacheTestManager.CacheTestManager;

def tC = testRunner.getTestCase();
def tS = tC.getTestSuite();
def tP = tS.getProject();
def tStep  = tC.getTestStepByName('Policy List GET for user');
def responseXML = tStep.httpRequest.response.getResponseContent();


def myXML = null;
if(responseXML != null)
{
	try
	{
		myXML = new XmlSlurper().parseText(responseXML);
	}
	catch(Throwable e)
	{
		log.info e;					
	}
}

for(i = 0; i < myXML.Policy.size(); i++)
{	
	if(myXML.Policy[i].PolicyClass.toString() == "urn:dece:type:policy:ParentalControl:AllowAdult")
	{
		tC.setPropertyValue("policyIdAA", myXML.Policy[i].@PolicyID.toString());
	}
	else if(myXML.Policy[i].PolicyClass.toString() == "urn:dece:type:policy:UserLinkConsent")
	{
		tC.setPropertyValue("policyIdULC", myXML.Policy[i].@PolicyID.toString());
	}
	else if(myXML.Policy[i].PolicyClass.toString() == "urn:dece:type:policy:TermsOfUse")
	{
		tC.setPropertyValue("policyIdTOU", myXML.Policy[i].@PolicyID.toString());
	}	
}


def toTCName = tC.getPropertyValue("tName");
def toTSName = tC.getPropertyValue("sName")


def toTC = testRunner.testCase.testSuite.project.getTestSuiteByName(toTSName).getTestCaseByName(toTCName);

toTC.setPropertyValue("policyIdAA",tC.getPropertyValue("policyIdAA"));
toTC.setPropertyValue("policyIdULC", tC.getPropertyValue("policyIdULC"));
toTC.setPropertyValue("policyIdTOU", tC.getPropertyValue("policyIdTOU"));

import com.eviware.soapui.model.testsuite.TestRunner.Status

def tC = testRunner.getTestCase();
def tS = tC.getTestSuite();
def tP = tS.getProject();
def versionMinor = context.expand( '${#Project#versionMinor}' )
def AccountUserCreateStepName = "Create Account User TOU ULC";
if(versionMinor == '11')
AccountUserCreateStepName = "Create Account User TOU ULC 11";


tC.setPropertyValue("expectedResponse", "Created");

def myTC = tP.getTestSuiteByName("COORD-2124").getTestCaseByName(AccountUserCreateStepName);

myTC.setPropertyValue("tName", tC.getName());
myTC.setPropertyValue("sName", tS.getName());

myTC.setPropertyValue("policyCreatorNodeId", "urn:dece:org:org:dece:"+tP.getPropertyValue("nodeIdRTL"));
tC.setPropertyValue("policyCreatorNodeId", "urn:dece:org:org:dece:"+tP.getPropertyValue("nodeIdRTL"));

def runner = myTC.run(null, false);

assert runner.status == Status.FINISHED;

import com.eviware.soapui.model.testsuite.TestRunner.Status;

def tC = testRunner.getTestCase();
def tS = tC.getTestSuite();
