String status = "";
String connection = "";
String contentType = "";
String contentLength = "";
String contentLocation = "";

tStep.httpRequest.response.responseHeaders.each() { header, value ->
	switch (header) {
		case "Location": 
			contentLocation = value[0];
			break;
		case "#status#": 
			status = value[0]; 
			break;
		case "Connection": 
			connection = value[0]; 
			break;
		case "Content-Type": 
			contentType = value[0]; 
			break;
		case "Content-Length": 
			contentLength = value[0]; 
			break;
		case "Transfer-Encoding": 
			contentEncoding = value[0]; 
			break;
	}
}

String versionMajor = tP.getPropertyValue("versionMajor");
String versionMinor = tP.getPropertyValue("versionMinor");
String restVersion = "rest/"+ versionMajor + "/" + versionMinor;

def nodeAccountId = tC.getPropertyValue("nodeAccountId");
def nodeUserId = tC.getPropertyValue("nodeUserId");

//def myURLFunctions = new urlFunctions(log);

def contentLocationURL = contentLocation.toURL();
def contentLocationURLStr = URLDecoder.decode(contentLocationURL.getPath());



int lastSlash = contentLocationURLStr.lastIndexOf(":");
def policyListOid = contentLocationURLStr.substring(lastSlash + 1);

tC.setPropertyValue("policyListOid", policyListOid);
tC.setPropertyValue("policyListId", "urn:dece:policylistid:org:dece:" + policyListOid);
log.info ("policyListId.."+policyListOid);
tC.setPropertyValue("policyListIdURL", URLEncoder.encode(tC.getPropertyValue("policyListId")));

tC.setPropertyValue("responseStatus", status);
tC.setPropertyValue("connectionStatus", connection);
tC.setPropertyValue("contentType", contentType);
tC.setPropertyValue("contentLength", contentLength);
tC.setPropertyValue("contentLocation", contentLocation);

