import org.openqa.selenium.By;
import org.openqa.selenium.NoSuchElementException;
import org.openqa.selenium.OutputType;
import org.openqa.selenium.TakesScreenshot;
import org.openqa.selenium.WebDriver;
import org.openqa.selenium.*;
import org.openqa.selenium.WebElement;
import org.openqa.selenium.firefox.FirefoxDriver;
import org.openqa.selenium.support.ui.Select;
import org.openqa.selenium.htmlunit.HtmlUnitDriver;
import com.gargoylesoftware.htmlunit.BrowserVersion;
import java.util.concurrent.TimeUnit;
import org.openqa.selenium.JavascriptExecutor;
boolean ULC=context.testCase.getTestStepAt(context.getCurrentStepIndex()).getLabel().contains('Create')
boolean onlyAssertion=true;
log.info "onlyAssertion = " + onlyAssertion;
log.info "ULC = " + ULC;
//If test step name contains create then it 'll create ULC other wise it will delete ULC
WebDriver driver = new FirefoxDriver();
driver.get("https://stdecqavutl1.va.neustar.com:2485/simplesaml/uvvuStoreFront.php");
driver.manage().timeouts().pageLoadTimeout(5000,TimeUnit.MILLISECONDS)
new Select(driver.findElement(By.id("targetEnvironment"))).selectByVisibleText("QALP1");
try{				
	new Select(driver.findElement(By.id("targetNode")))
	.selectByVisibleText("RET");}catch (TimeoutException e){
		log.info "In CATCH Block... "
		((JavascriptExecutor) driver).executeScript("window.stop();");
	}

driver.manage().timeouts().pageLoadTimeout(30,TimeUnit.SECONDS)
if(!onlyAssertion)
{
log.info "Beofore Iframe"				
WebElement iframe = driver.findElement(By.tagName("iframe"));
log.info "After Iframe"

driver.switchTo().frame(iframe);
new Select(driver.findElement(By.name("controlFile"))).selectByVisibleText("ULC");
driver.findElement(By.xpath("(//input[@name='userAction'])[4]")).click();

log.info "Loading ULC"	
driver.findElement(By.name("userAction")).click();
}
driver.switchTo().defaultContent();
driver.findElement(By.xpath("//*[@id='uvvuStoreFront']/a[2]")).click();

driver.findElement(By.id("userName1")).clear();
def userUsername = context.expand( '${#TestCase#policyCreatorUsername}' )
def userPassword = context.expand( '${#TestCase#userPassword}' )
driver.findElement(By.id("userName1")).sendKeys(userUsername);
driver.findElement(By.id("password1")).sendKeys(userPassword);

if(!ULC && !onlyAssertion)
{
	log.info "Since test step name is not having 'Create' text, hence deleting the ULC by unchecking the CheckBox"; 
	driver.findElement(By.xpath("//*[@id='linkAccount']")).click();
}
else
log.info "Since test step name is having 'Create' text, hence Creating the ULC";

//}

driver.findElement(By.id("submitButtonDiv")).click();
def AssertionUUID = driver.findElement(By.xpath("//b/table/tbody/tr/td[2]/font")).getText()
def NodeAccountID = driver.findElement(By.xpath("//b/table/tbody/tr[2]/td[2]/font")).getText()
def NodeUserID = driver.findElement(By.xpath("//b/table/tbody/tr[3]/td[2]/font")).getText()
def SAMLString = driver.findElement(By.xpath("//b[2]/b/b/b/textarea")).getText()

testRunner.getTestCase().setPropertyValue('samlAssertion',SAMLString);

log.info AssertionUUID
log.info NodeAccountID
log.info NodeUserID
log.info SAMLString


driver.quit();

