import groovy.sql.Sql;

def testCase  = testRunner.getTestCase();
def testSuite = testCase.getTestSuite();
def testProject = testSuite.getProject();

def userName = testProject.getPropertyValue("DBUsername")
def passWord = testProject.getPropertyValue("DBPassword")
def jDBCConnStr = context.expand( '${#Project#JDBCConnStr}')
def jDBCDriver = context.expand( '${#Project#JDBCDriver}' )
def db = [url:jDBCConnStr, user: userName, password: passWord, driver:jDBCDriver];

def sql = Sql.newInstance(db.url, db.user, db.password, db.driver);

String uuid = testProject.getPropertyValue("UUID");
String nodeUserSAMLAssertion;
String nodeOid;
String nodeAccountOid;
String nodeUserOid;
String accountOid;
String userOid;

Integer recordCount = 0;
boolean iFailed = false;

sqlStr = """select 
rawtohex(nusa.NODE_OID), 
to_char(nusa.NODE_USER_SAML_ASSERTION), 
rawtohex(na.NODE_ACCOUNT_ID),
rawtohex(nu.NODE_USER_ID),
rawtohex(na.ACCOUNT_OID),
rawtohex(nu.USER_OID)
from 
NODE_USER_SAML_ASSERTION nusa,
NODE_ACCOUNT na,
NODE_USER nu
where 
nusa.NODE_USER_SAML_UUID = $uuid
and 
na.row_OID = nusa.NODE_ACCOUNT_ID
and 
nu.row_oid = nusa.NODE_USER_ID""";

sql.eachRow(sqlStr){
	recordCount = recordCount + 1;
	nodeOid = it[0];
	nodeUserSAMLAssertion = it[1];	
	nodeAccountOid = it[2];
	nodeUserOid = it[3];
	accountOid = it[4];
	userOid = it[5];
}

try
{
	assert recordCount == 1;
}
catch(Throwable e)
{
	log.info e;
	log.info "1";
	log.info recordCount;	
	iFailed = true;
}

assert iFailed == false;

testProject.setPropertyValue("NodeUserSAMLAssertion", nodeUserSAMLAssertion);
testProject.setPropertyValue("NodeOid", nodeOid);
testProject.setPropertyValue("NodeAccountOid", nodeAccountOid);
testProject.setPropertyValue("NodeUserOid", nodeUserOid);
testProject.setPropertyValue("AccountOid", accountOid);
testProject.setPropertyValue("UserOid", userOid);

testProject.setPropertyValue("NodeAccountId", "urn:dece:accountid:org:dece:" + nodeAccountOid);


testProject.setPropertyValue("NodeAccountID",URLEncoder.encode(testProject.getPropertyValue("NodeAccountID"), "UTF-8"));

testProject.setPropertyValue("NodeUserId", "urn:dece:userid:org:dece:" + nodeUserOid);
testProject.setPropertyValue("AccountId", "urn:dece:accountid:org:dece:" + accountOid);

sql.close();

log.info "Woohoo!";

