	log.info actualCountDMT;	
	iFailed = true;
}

sqlStr = """select 
to_char(rawtohex(dmt.RIGHTS_TOKEN_OID)),
to_char(rawtohex(dmt.ACCOUNT_OID)),
to_char(rawtohex(dmt.USER_OID)),
to_char(rawtohex(dmt.CREATED_BY)),
to_char(dmt.STATE),
to_char(dmt.STATUS),
to_char(dmt.EXPIRATION_DATE, 'yyyy-mm-dd hh:mi:ss') EXPIRATION_DATE
from 
DISCRETE_MEDIA_TOKEN dmt  
where 
dmt.RIGHTS_TOKEN_OID = hextoraw('$rightsTokenOid')
and 
dmt.ACCOUNT_OID = hextoraw('$accountOid')
and
dmt.STATE = 'leased'""";

log.info sqlStr;

sql.eachRow(sqlStr){	
	recordCount = recordCount + 1;
	RIGHTS_TOKEN_OID = it[0];
	ACCOUNT_OID = it[1];
	USER_OID = it[2];
	log.info 'USER_OID=='+USER_OID
	CREATED_BY = it[3];
	TYPE = it[4];
	STATUS = it[5];
	EXPIRATION_DATE = it[6];
}

try
{
	assert recordCount == 1;
}
catch(Throwable e)
{
	log.info e;
	log.info "1";
	log.info recordCount;	
	iFailed = true;
}

try
{
	assert rightsTokenOid == RIGHTS_TOKEN_OID;
}
catch(Throwable e)
{
	log.info e;
	log.info rightsTokenOid;
	log.info RIGHTS_TOKEN_OID;	
	iFailed = true;
}

try
{
	assert accountOid == ACCOUNT_OID;
}
catch(Throwable e)
{
	log.info e;
	log.info accountOid;
	log.info ACCOUNT_OID;	
	iFailed = true;
}

try
{
	assert userOid == USER_OID;
}
catch(Throwable e)
{
	log.info e;
	log.info userOid;
	log.info USER_OID;	
	iFailed = true;
}

try
{
	//assert nodeOid == CREATED_BY;
}
catch(Throwable e)
{
	log.info e;
	log.info nodeOid;
	log.info CREATED_BY;	
	iFailed = true;
}

try
{
	assert leaseType == TYPE;
}
catch(Throwable e)
{
	log.info e;
	log.info leaseType;
	log.info TYPE;	
	iFailed = true;
}

try
{
	assert leaseStatus == STATUS;
}
catch(Throwable e)
{
	log.info e;
