	log.info STATUS;	
	iFailed = true;
}

try
{
	assert leaseExpirationDate == EXPIRATION_DATE;
}
catch(Throwable e)
{
	log.info e;
	log.info leaseExpirationDate;
	log.info EXPIRATION_DATE;	
	iFailed = true;
}

