            		catch(Throwable e)
            		{
            			log.info e;
            			log.info DiscreteMediaTokenIDExp;
            			log.info StatusExp;
            			log.info StatusAct;
            			iFailed = true;
            		}   

            		if(ModificationDateExp != null)
    				{        			
                		ModificationDateAct = actDMT.ResourceStatus.Current.@ModificationDate.text().replace("T", " ").replace("Z", "");
                		try
