{ 
	testProject.setPropertyValue("DiscreteMediaRightsRemainingHD", "1")
	testProject.setPropertyValue("DiscreteMediaRightsRemainingSD", "0")
	testProject.setPropertyValue("DiscreteMediaRightsRemainingPD", "0")
}
else if (testCase.getPropertyValue("ContentProfile")  == "urn:dece:type:mediaprofile:sd") 
{ 
	testProject.setPropertyValue("DiscreteMediaRightsRemainingHD", "0")
	testProject.setPropertyValue("DiscreteMediaRightsRemainingSD", "1")
	testProject.setPropertyValue("DiscreteMediaRightsRemainingPD", "0")
}
else (testCase.getPropertyValue("ContentProfile")  == "urn:dece:type:mediaprofile:pd") 
{ 
	testProject.setPropertyValue("DiscreteMediaRightsRemainingHD", "0")
	testProject.setPropertyValue("DiscreteMediaRightsRemainingSD", "0")
	testProject.setPropertyValue("DiscreteMediaRightsRemainingPD", "1")
}

