 else
 {

	assert contentLocationURL.getPath().matches("/rest/$major/$minor/Account/" + nodeAccountId + "/RightsToken/urn%3Adece%3Arightstokenid%3Aorg%3Adece%3A" + "[0-9A-Z]*");

