def testProject = testSuite.getProject();

def userName = testProject.getPropertyValue("DBUsername")
def passWord = testProject.getPropertyValue("DBPassword")
def jDBCConnStr = context.expand( '${#Project#JDBCConnStr}')
def jDBCDriver = context.expand( '${#Project#JDBCDriver}' )
def db = [url:jDBCConnStr, user: userName, password: passWord, driver:jDBCDriver];
def sql = Sql.newInstance(db.url, db.user, db.password, db.driver);

//Fetch Rights Token Oid of some different account.
def rightTokenOid = testCase.getPropertyValue("RightsTokenOid");
sqlStr = """select 
rawtohex(RIGHTS_TOKEN_OID)
from 
rights_token 
where 
rights_token_oid not in ( hextoraw('$rightTokenOid')) and rownum<2""";
sql.eachRow(sqlStr){
	RightsTokenOid = it[0];
}
//set rights token oid and rights token id that is fetched.
testCase.setPropertyValue("RightsTokenOid",RightsTokenOid);
def rightTokenId = "urn:dece:rightstokenid:org:dece:"+RightsTokenOid;
testCase.setPropertyValue("RightsTokenId",rightTokenId);

