sqlStr1 ="""select 
status_id 
from
rights_token 
where 
status_id not in ('active')and rownum<2""";
sql.eachRow(sqlStr1){
	RightsTokenStatus = it[0];
}

def rightsTokenStatus = RightsTokenStatus;
def rightsTokenOid = testCase.getPropertyValue("RightsTokenId").split(":")[5];
sqlStr = """update rights_token set status_id = '$rightsTokenStatus' where rights_token_oid = hextoraw('$rightsTokenOid')""";
