def tC  = testRunner.getTestCase();
def tS = tC.getTestSuite();
def tP = tS.getProject();

tC.setPropertyValue("policyClass","urn:dece:type:policy:LockerViewAllConsent");

def tC = testRunner.getTestCase();
def tS = tC.getTestSuite();
def tP = tS.getProject();
def tStep  = tC.getTestStepByName('LVAC GET');
def responseXML = tStep.httpRequest.response.getResponseContent();
def callTSname = tC.getPropertyValue("sName");
def callTCname = tC.getPropertyValue("tName");

//def callTC = tP.getTestSuiteByName(callTSname).getTestCaseByName(callTCname);

String status = "";
String connection = "";
String contentType = "";
String contentLength = "";
String contentLocation = "";

tStep.httpRequest.response.responseHeaders.each() 
{ 
                header, value ->
                                switch (header) 
                                {
                                                case "Location": 
                                                                contentLocation = value[0];
                                                                break;
                                                case "#status#": 
                                                                status = value[0]; 
                                                                break;
                                                case "Connection": 
                                                                connection = value[0]; 
                                                                break;
                                                case "Content-Type": 
                                                                contentType = value[0]; 
                                                                break;
                                                case "Content-Length": 
                                                                contentLength = value[0]; 
                                                                break;
                                                case "Transfer-Encoding": 
                                                                contentEncoding = value[0]; 
                                                                break;
                                }
}

def myXML = null;
if(responseXML != null)
{
                try
                {
                                myXML = new XmlSlurper().parseText(responseXML);

                }
                catch(Throwable e)
                {
                                log.info e;                                                                            
                }
}

log.info "Result set from response ..." + myXML;

String policyId;
for (int i =0 ; i<12 ; i++){
	def requestingEntity = myXML.Policy[i].RequestingEntity.toString();
	log.info "requestingEntity : " + requestingEntity;
	if(requestingEntity.equals("urn:dece:org:org:dece:RET_CS_1")){
		policyId = myXML.Policy[i].@PolicyID.text();
		log.info "Policy ID :"+policyId;
		break;	
	}
}


//String policyId = myXML.Policy[0].@PolicyID.toString();
String policyOid = "";
if(policyId != "" && policyId != null)
{
                policyOid = policyId.split("urn:dece:policyid:org:dece:")[1];

}

tC.setPropertyValue("policyId", policyId);
tC.setPropertyValue("policyOid", policyOid);

//def myURLFunctions = new urlFunctions();

tC.setPropertyValue("policyIdURL",(policyId));
log.info tC.getPropertyValue("policyIdURL");
//callTC.setPropertyValue("policyId", URLEncoder.encode(policyOid));
//callTC.setPropertyValue("policyIdURL", URLEncoder.encode(policyId));

