	tC.setPropertyValue("Policy_Class", "urn:dece:type:policy:TermsOfUse");
}else{
	tC.setPropertyValue("Policy_Class", URLEncoder.encode("urn:dece:type:policy:TermsOfUse", "UTF-8"));
}

