			case "#status#": 
				respstatus = value[0]; 
				break;
			case "Connection": 
				respconnection = value[0]; 
				break;
			case "Content-Type": 
				respcontentType = value[0]; 
				break;
			case "Content-Length": 
				respcontentLength = value[0]; 
				break;
