					stsaudienceList = stsaudienceList.minus(it);
				}
			}
		}
	}

	if (stsaudienceList.size() >= 1) {
		log.info "DEBUG: Adding stsaudienceList";
		affiliationList.addAll(stsaudienceList);
	}
} 

