	if (tC.getPropertyValue("actionAuthHeader") == "EXCLUDE") {
	tC.setPropertyValue("authHeaderValue",null);
}

	if (tC.getPropertyValue("actionDeviceAuthHeader") == "EXCLUDE") {
	tC.setPropertyValue("deviceAuthHeaderValue",null);
}

