	callTC.setPropertyValue("NODE_ACCOUNT_ID_FULL",tC.getPropertyValue("assertionAccountIdFULL"));
	callTC.setPropertyValue("NODE_USER_ID_FULL",tC.getPropertyValue("assertionUserIdFULL"));
}else{
