		assert u.length() > 0;
		assert a.length() > 0;

		tC.setPropertyValue("USER_OID", u);
		tC.setPropertyValue("ACCOUNT_OID", a);
	}
	catch(Throwable e) {
		log.info "ERROR - dbUtils.userGetUserOID - $e";
		assert 1 == 2;		
	}
