switch (errorRespType) {

	case "SUCCESS":
		expectHTTPstatus = "HTTP/1.1 201 Created";
		log.info "Res_RTObjType[$Res_RTObjType]"+ "xxxxxxxxxxxxxxx";
		//log.info "myNodeRole[$myNodeRole]"+ "xxxxxxxxxxxxxxx";
